// src/App.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert } from 'react-bootstrap';
import FileUpload from './components/FileUpload';
import ColumnSelector from './components/ColumnSelector';
import ResultsDisplay from './components/ResultsDisplay';
import { analyzeData } from './services/api'; // analyzeData espera nombres LIMPIOS ahora
import './styles/App.css';

function App() {
  // --- Estados ---
  const [uploadedFile, setUploadedFile] = useState(null); // { filename, filepath }
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);
  const [availableColumns, setAvailableColumns] = useState([]); // Nombres ORIGINALES para mostrar
  const [selectedColumns, setSelectedColumns] = useState([]); // Nombres ORIGINALES seleccionados por usuario
  const [columnMapping, setColumnMapping] = useState({}); // NUEVO: Mapeo original -> limpio
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // --- Efectos ---
  useEffect(() => {
      if (!uploadedFile) {
          setAvailableColumns([]);
          setSelectedColumns([]);
          setColumnMapping({}); // Limpiar mapeo también
          setAnalysisResults(null);
          setError(null);
          setIsAnalyzing(false);
      }
  }, [uploadedFile]);

  // --- Manejadores ---
  const handleFileUploadSuccess = (uploadData) => {
    console.log("File upload successful in App:", uploadData);
    setUploadedFile({
      filename: uploadData.filename,
      filepath: uploadData.filepath
    });
    // Guardar columnas ORIGINALES para mostrar y el MAPEO
    setAvailableColumns(uploadData.original_columns || []);
    setColumnMapping(uploadData.column_mapping || {}); // Guardar el mapeo
    // Resetear estados
    setSelectedColumns([]);
    setAnalysisResults(null);
    setError(null);
    setIsAnalyzing(false);
  };

  // Actualiza selectedColumns (sigue guardando nombres originales)
  const handleColumnSelectionChange = (newSelectedOriginalColumns) => {
    setSelectedColumns(newSelectedOriginalColumns);
    setAnalysisResults(null);
    setError(null);
  };

  // Iniciar análisis
  const handleStartAnalysis = async () => {
    if (!uploadedFile || !uploadedFile.filepath) { /*...*/ return; }
    if (selectedColumns.length === 0) { /*...*/ return; }

    // --- NUEVO: Traducir nombres originales a limpios usando el mapeo ---
    const cleanedSelectedColumns = selectedColumns.map(originalName => columnMapping[originalName]).filter(Boolean); // filter(Boolean) por si algo falla en el mapeo

    // Validar si la traducción dio un resultado inesperado
    if (cleanedSelectedColumns.length !== selectedColumns.length) {
         console.warn("Mismatch between original and cleaned selected columns", selectedColumns, cleanedSelectedColumns);
         setError("Error translating selected column names. Please try re-uploading.");
         return;
    }
    if (cleanedSelectedColumns.length === 0) {
         setError("No valid columns selected after cleaning names.");
         return;
    }
    // ----------------------------------------------------------------

    console.log("Starting analysis with:", uploadedFile.filepath, "Cleaned columns:", cleanedSelectedColumns);
    setIsAnalyzing(true);
    setError(null);
    setAnalysisResults(null);

    try {
      // --- Enviar NOMBRES LIMPIOS al servicio API ---
      const data = await analyzeData(uploadedFile.filepath, cleanedSelectedColumns);
      // ---------------------------------------------
      console.log("Analysis data received in App:", data);
      setAnalysisResults(data);
    } catch (err) {
       const errorMsg = err.response?.data?.error || err.message || "Analysis failed.";
       console.error("Analysis error caught in App:", err);
       setError(errorMsg);
       setAnalysisResults(null);
    } finally {
      setIsAnalyzing(false);
    }
  };

  // --- Renderizado ---
  return (
    <div className="app">
      <Container fluid="lg">
        {/* Encabezado */}
        <Row className="header">
           {/* ... (sin cambios) ... */}
           <h1>Anomaly Detection System</h1>
           <p>Upload financial data, select relevant columns, and run analysis.</p>
        </Row>

        {/* Alerta de Error Global */}
        {error && ( <Row className="mb-3"><Col><Alert variant="danger" onClose={() => setError(null)} dismissible>{error}</Alert></Col></Row> )}

        {/* Sección Principal */}
        <Row className="mt-2">
          {/* Columna Izquierda: Subida */}
          <Col md={5} lg={4} className="mb-3 mb-md-0">
            <Card className="upload-card h-100">
              <Card.Body className="d-flex flex-column">
                <Card.Title><span className="badge bg-secondary me-1">1</span> Upload Data</Card.Title>
                <FileUpload
                  onUploadSuccess={handleFileUploadSuccess}
                  setError={setError}
                  clearUploadedFile={() => setUploadedFile(null)}
                />
              </Card.Body>
            </Card>
          </Col>

          {/* Columna Derecha: Selección/Análisis */}
          <Col md={7} lg={8}>
            <Card className="analysis-card h-100">
              <Card.Body className="d-flex flex-column">
                <Card.Title><span className="badge bg-secondary me-1">2</span> Select Columns & Analyze</Card.Title>

                {/* Mostrar Selector si hay archivo y columnas disponibles */}
                {uploadedFile && availableColumns.length > 0 ? (
                  <>
                    <div className="column-selector-wrapper mb-3" style={{ flexGrow: 1, maxHeight: '300px', overflowY: 'auto', border: '1px solid #eee', padding: '10px', borderRadius:'5px' }}>
                      {/* ColumnSelector sigue mostrando los nombres ORIGINALES */}
                      <ColumnSelector
                        columns={availableColumns} // Pasa nombres originales
                        selectedColumns={selectedColumns} // Pasa nombres originales seleccionados
                        onChange={handleColumnSelectionChange}
                      />
                    </div>
                    {/* Botón Analizar */}
                    <div className="mt-auto">
                       <Button
                          variant="success"
                          onClick={handleStartAnalysis}
                          disabled={isAnalyzing || selectedColumns.length === 0}
                          className="analyze-btn w-100"
                       >
                          {isAnalyzing ? (
                            <> <Spinner animation="border" size="sm" className="me-2" /> Analyzing... </>
                          ) : (
                            `Analyze ${selectedColumns.length} Selected Column(s)`
                          )}
                       </Button>
                     </div>
                  </>
                ) : (
                   <div className="text-center text-muted d-flex align-items-center justify-content-center h-100">
                       <p>{uploadedFile ? "Processing columns..." : "Upload a file to select columns."}</p>
                   </div>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Indicador de Carga Global */}
         {isAnalyzing && ( <Row className="mt-4 text-center"><Col><Spinner animation="border" variant="primary" /><p className="mt-2 text-primary">Analyzing data...</p></Col></Row> )}

        {/* Sección de Resultados */}
        {!isAnalyzing && analysisResults && (
          <Row className="mt-4">
            <Col>
              <ResultsDisplay data={analysisResults} />
            </Col>
          </Row>
        )}

      </Container>
    </div>
  );
}

export default App;
