// src/App.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Alert } from 'react-bootstrap';
import FileUpload from './components/FileUpload';
import ColumnSelector from './components/ColumnSelector';
import ResultsDisplay from './components/ResultsDisplay';
import { analyzeData } from './services/api'; // Importar solo analyzeData aquí
import './styles/App.css';
import unicodedata from 'unicodedata';

function App() {
  // --- Estados ---
  const [uploadedFile, setUploadedFile] = useState(null); // { filename, filepath }
  const [availableColumns, setAvailableColumns] = useState([]); // Columnas del archivo
  const [selectedColumns, setSelectedColumns] = useState([]); // Columnas seleccionadas
  const [analysisResults, setAnalysisResults] = useState(null); // { results: {...}, plots: {...} }
  const [isAnalyzing, setIsAnalyzing] = useState(false); // Estado de carga para análisis
  const [error, setError] = useState(null); // Mensaje de error general
  const [columnMap, setColumnMap] = useState({});

  // --- Efectos ---
  // Limpia estados dependientes si el archivo subido se quita
  useEffect(() => {
      if (!uploadedFile) {
          setAvailableColumns([]);
          setSelectedColumns([]);
          setAnalysisResults(null);
          setError(null);
          setIsAnalyzing(false);
      }
  }, [uploadedFile]);

  // --- Manejadores ---
  // Callback para FileUpload: se ejecuta tras subida exitosa
  const handleFileUploadSuccess = (uploadData) => {
    console.log("File upload successful in App:", uploadData);
    setUploadedFile({ // Guardar info del archivo
      filename: uploadData.filename,
      filepath: uploadData.filepath // ¡Importante! Necesario para analizar
    });
    setAvailableColumns(uploadData.columns || []); // Guardar columnas disponibles
    setColumnMap(uploadData.column_map || {});
    setSelectedColumns([]); // Resetear selección previa
    setAnalysisResults(null); // Resetear resultados previos
    setError(null); // Limpiar errores previos
    setIsAnalyzing(false); // Asegurar que no esté analizando
  };

  // Callback para ColumnSelector: actualiza columnas seleccionadas
  const handleColumnSelectionChange = (newSelectedColumns) => {
    setSelectedColumns(newSelectedColumns);
    // Limpiar resultados si cambia la selección para evitar inconsistencias
    setAnalysisResults(null);
    setError(null); // Limpiar error si se cambia selección
  };

  // Función para iniciar el análisis (disparada por el botón)
  const handleStartAnalysis = async () => {
    if (!uploadedFile || !uploadedFile.filepath) {
      setError("Internal error: File path missing after upload.");
      return;
    }
    if (selectedColumns.length === 0) {
      setError("Please select at least one column to analyze.");
      return;
    }

    // Traduce los nombres seleccionados a los nombres limpios
    const cleanedSelectedColumns = selectedColumns.map(col => columnMap[col] || col);

    console.log("Starting analysis with:", uploadedFile.filepath, cleanedSelectedColumns);
    setIsAnalyzing(true);
    setError(null);
    setAnalysisResults(null); // Limpiar resultados previos

    try {
      // Llamar al servicio API
      const data = await analyzeData(uploadedFile.filepath, cleanedSelectedColumns);
      console.log("Analysis data received in App:", data);
      setAnalysisResults(data); // Guardar resultados (incluye 'results' y 'plots')
    } catch (err) {
       // Manejar error devuelto por el servicio API
       const errorMsg = err.response?.data?.error || err.message || "Analysis failed.";
       console.error("Analysis error caught in App:", err);
       setError(errorMsg);
       setAnalysisResults(null); // Asegurar que no haya resultados parciales
    } finally {
      setIsAnalyzing(false); // Finalizar estado de carga
    }
  };

  // --- Renderizado ---
  return (
    <div className="app">
      <Container fluid="lg"> {/* Usar fluid=lg para más ancho en pantallas grandes */}

        {/* Encabezado */}
        <Row className="header">
          <Col>
            <h1>Anomaly Detection System</h1>
            <p>Upload financial data, select relevant columns, and run analysis.</p>
          </Col>
        </Row>

        {/* Alerta de Error Global (si existe) */}
        {error && (
            <Row className="mb-3">
                <Col>
                    {/* 'dismissible' permite cerrar la alerta */}
                    <Alert variant="danger" onClose={() => setError(null)} dismissible>
                        <Alert.Heading>Error</Alert.Heading>
                        <p>{error}</p>
                    </Alert>
                </Col>
            </Row>
        )}

        {/* Sección Principal: Subida y Selección/Análisis */}
        <Row className="mt-2">
          {/* Columna Izquierda: Subida */}
          <Col md={5} lg={4} className="mb-3 mb-md-0"> {/* Ajustar tamaño columnas */}
            <Card className="upload-card h-100"> {/* h-100 para igualar altura */}
              <Card.Body className="d-flex flex-column"> {/* Flex para alinear botón */}
                <Card.Title><span className="badge bg-secondary me-1">1</span> Upload Data</Card.Title>
                <FileUpload
                  onUploadSuccess={handleFileUploadSuccess}
                  setError={setError} // Pasar setError para que FileUpload reporte errores de subida
                  // Pasar función para limpiar estado si se deselecciona archivo
                  clearUploadedFile={() => setUploadedFile(null)}
                />
              </Card.Body>
            </Card>
          </Col>

          {/* Columna Derecha: Selección de Columnas y Botón Analizar */}
          <Col md={7} lg={8}>
            <Card className="analysis-card h-100">
              <Card.Body className="d-flex flex-column">
                <Card.Title><span className="badge bg-secondary me-1">2</span> Select Columns & Analyze</Card.Title>

                {/* Mostrar Selector o mensaje si no hay archivo */}
                {uploadedFile && availableColumns.length > 0 ? (
                  <>
                    {/* Contenedor con scroll para lista larga de columnas */}
                    <div className="column-selector-wrapper mb-3" style={{ flexGrow: 1, maxHeight: '300px', overflowY: 'auto', border: '1px solid #eee', padding: '10px', borderRadius:'5px' }}>
                      <ColumnSelector
                        columns={availableColumns}
                        selectedColumns={selectedColumns}
                        onChange={handleColumnSelectionChange}
                      />
                    </div>

                    {/* Botón para iniciar análisis */}
                    <div className="mt-auto"> {/* Empujar botón al fondo */}
                       <Button
                          variant="success"
                          onClick={handleStartAnalysis}
                          disabled={isAnalyzing || selectedColumns.length === 0} // Deshabilitado
                          className="analyze-btn w-100" // Ancho completo
                       >
                          {isAnalyzing ? (
                            <> <Spinner animation="border" size="sm" className="me-2" /> Analyzing... </>
                          ) : (
                            `Analyze ${selectedColumns.length} Selected Column(s)` // Texto dinámico
                          )}
                       </Button>
                     </div>
                  </>
                ) : (
                   // Mensaje si no hay archivo subido
                   <div className="text-center text-muted d-flex align-items-center justify-content-center h-100">
                       <p>{uploadedFile ? "No columns found in the uploaded file." : "Upload a file to select columns for analysis."}</p>
                   </div>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Indicador de Carga Global durante el análisis */}
         {isAnalyzing && (
              <Row className="mt-4 text-center">
                   <Col>
                        <Spinner animation="border" variant="primary" role="status">
                           <span className="visually-hidden">Analyzing...</span>
                        </Spinner>
                        <p className="mt-2 text-primary">Analyzing data, this may take a moment...</p>
                   </Col>
              </Row>
         )}

        {/* Sección de Resultados (Mostrar si no está analizando y hay resultados) */}
        {!isAnalyzing && analysisResults && (
          <Row className="mt-4">
            <Col>
              {/* Usar el nuevo componente para mostrar resultados */}
              <ResultsDisplay data={analysisResults} />
            </Col>
          </Row>
        )}

      </Container>
    </div>
  );
}

export default App;
