// src/components/ResultsDisplay.js
import React from 'react';
import { Card, Row, Col, Table, Image, Alert } from 'react-bootstrap';

/**
 * Componente para mostrar los resultados del análisis, incluyendo gráficos orientados al cliente.
 * @param {object} props
 * @param {object} props.data - El objeto completo devuelto por /api/analyze (contiene 'results' y 'plots').
 */
const ResultsDisplay = ({ data }) => {
  const results = data?.results;
  const plots = data?.plots; // Diccionario con los gráficos base64: anomaly_summary_pie, top_anomaly_categories, anomaly_numerical_range, anomaly_score_severity

  if (!results || typeof results !== 'object') {
    return (
        <Card className="results-card mt-4">
             <Card.Body><Card.Title>Analysis Results</Card.Title><p className="text-muted fst-italic">Waiting for analysis results or analysis returned invalid data.</p></Card.Body>
        </Card>
    );
  }

  // Función auxiliar para formatear valores en la tabla
  const formatValue = (value) => {
    if (value === null || typeof value === 'undefined') return <span className="text-muted">N/A</span>;
    if (typeof value === 'number') {
      if (value % 1 !== 0) {
          if (Math.abs(value) > 1e6 || (Math.abs(value) < 1e-3 && value !== 0)) return value.toExponential(2);
          return value.toFixed(4);
      }
      return value.toLocaleString();
    }
    if (typeof value === 'boolean') { return value ? <span className="badge bg-danger">Yes</span> : <span className="badge bg-secondary">No</span>; }
    const strValue = String(value);
    if (strValue.length > 100) { return strValue.substring(0, 97) + '...'; }
    return strValue;
  };

  // Función para formatear nombres de columnas (Título Amigable)
  const formatTitle = (key) => key?.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()) ?? 'N/A';

  const anomalyRecords = results.sample_anomalous_records || [];
  const anomalyColumns = anomalyRecords.length > 0 ? Object.keys(anomalyRecords[0]) : [];

  // Nombres de las columnas usadas para gráficos (para títulos)
  const plottedNumCol = results.plotted_numerical_feature;
  const plottedCatCol = results.plotted_categorical_feature;

  return (
    <Card className="results-card mt-4 shadow-sm">
      <Card.Header as="h5">Analysis Results</Card.Header>
      <Card.Body>
        {/* Resumen Numérico (Sin Cambios) */}
        <Alert variant="light" className="results-summary border mb-4">
            <Row className="align-items-center">
                <Col md={4} className="mb-2 mb-md-0 border-end"><div><strong>Records Analyzed:</strong></div><div className="fs-5">{results.total_records_analyzed?.toLocaleString() ?? 'N/A'}</div></Col>
                <Col md={4} className="mb-2 mb-md-0 border-end"><div className="text-danger"><strong>Anomalies Found:</strong></div><div className="fs-5 text-danger">{results.detected_anomalies_count?.toLocaleString() ?? 'N/A'} ({results.detected_anomalies_percentage ?? 'N/A'}%)</div></Col>
                <Col md={4} className="mb-2 mb-md-0"><div> <strong>Threshold (MSE):</strong></div><div className="fs-5">{(results.anomaly_threshold ?? 0).toExponential(4)}</div></Col>
            </Row>
             <hr />
             <Row><Col><small><strong>Columns Used in Analysis:</strong> {results.columns_analyzed?.join(', ') ?? 'N/A'}</small></Col></Row>
        </Alert>

        {/* --- Nueva Sección de Gráficos --- */}
        {(plots && Object.keys(plots).length > 0) ? (
          <div className="plots-container mb-4">
            <h5 className="mb-3">Analysis Insights</h5>
            <Row>

              {/* Gráfico 1: Resumen Pie Chart */}
              {plots.anomaly_summary_pie && (
                <Col md={6} lg={4} className="mb-4">
                   <Card className="h-100">
                     <Card.Header className="text-center small fw-bold">Overall Summary</Card.Header>
                     <Card.Body className="text-center d-flex flex-column justify-content-center">
                        <Image src={`data:image/png;base64,${plots.anomaly_summary_pie}`} fluid thumbnail alt="Anomaly Summary Pie Chart" />
                        <p className="text-muted small mt-2">Percentage of normal vs. potentially anomalous records.</p>
                     </Card.Body>
                   </Card>
                </Col>
              )}

              {/* Gráfico 4: Severidad (Bar Chart) */}
              {plots.anomaly_score_severity && (
                 <Col md={6} lg={4} className="mb-4">
                   <Card className="h-100">
                     <Card.Header className="text-center small fw-bold">Anomaly Severity Levels</Card.Header>
                     <Card.Body className="text-center d-flex flex-column justify-content-center">
                        <Image src={`data:image/png;base64,${plots.anomaly_score_severity}`} fluid thumbnail alt="Anomaly Severity Levels" />
                         <p className="text-muted small mt-2">Groups anomalous records by their anomaly score level (higher score means more unusual).</p>
                     </Card.Body>
                   </Card>
                 </Col>
              )}

              {/* Gráfico 2: Top Categorías (Bar Chart) */}
              {plots.top_anomaly_categories && plottedCatCol && (
                 <Col md={6} lg={4} className="mb-4">
                   <Card className="h-100">
                     <Card.Header className="text-center small fw-bold">Top "{formatTitle(plottedCatCol)}" in Anomalies</Card.Header>
                     <Card.Body className="text-center d-flex flex-column justify-content-center">
                        <Image src={`data:image/png;base64,${plots.top_anomaly_categories}`} fluid thumbnail alt={`Top ${formatTitle(plottedCatCol)} in Anomalies`} />
                        <p className="text-muted small mt-2">Shows the most frequent categories for "{formatTitle(plottedCatCol)}" found among the anomalous records.</p>
                     </Card.Body>
                   </Card>
                 </Col>
              )}

              {/* Gráfico 3: Rango Numérico (Box/Violin Plot) */}
              {plots.anomaly_numerical_range && plottedNumCol && (
                <Col md={6} lg={4} className="mb-4">
                   <Card className="h-100">
                     <Card.Header className="text-center small fw-bold">Range of "{formatTitle(plottedNumCol)}" in Anomalies</Card.Header>
                     <Card.Body className="text-center d-flex flex-column justify-content-center">
                        <Image src={`data:image/png;base64,${plots.anomaly_numerical_range}`} fluid thumbnail alt={`Range of ${formatTitle(plottedNumCol)} in Anomalies`} />
                        <p className="text-muted small mt-2">Shows the distribution (range, median, quartiles) for "{formatTitle(plottedNumCol)}" within anomalous records.</p>
                     </Card.Body>
                   </Card>
                 </Col>
              )}

            </Row>
          </div>
        ) : (
             // Mensaje si NO hay gráficos (incluso si hay resultados)
            <Alert variant="light" className="text-center border fst-italic">No graphical insights could be generated for this analysis.</Alert>
        )}
        {/* --- Fin Nueva Sección de Gráficos --- */}


        {/* Tabla de Registros Anómalos (Sin Cambios) */}
        <div className="anomaly-records mt-4">
            <h5 className="mb-3">Sample Anomalous Records <small className="text-muted">(Top 10 by Anomaly Score)</small></h5>
            {anomalyRecords.length > 0 ? (
             <div className="table-responsive">
                <Table striped bordered hover responsive="sm" size="sm">
                 <thead className="table-light">
                     <tr>{anomalyColumns.map((key) => (<th key={key}>{formatTitle(key)}</th>))}</tr>
                 </thead>
                 <tbody>
                 {anomalyRecords.map((record, index) => (
                    <tr key={record.original_index ?? `anomaly-${index}`}>
                     {anomalyColumns.map((key) => (
                         <td key={`${record.original_index ?? index}-${key}`} className={key === 'anomaly_score_mse' ? 'fw-bold text-danger' : (key === 'is_anomaly' ? 'text-center' : '')}>
                           {formatValue(record[key])}
                         </td>
                     ))}
                    </tr>
                  ))}
                 </tbody>
                </Table>
              </div>
            ) : (
                <Alert variant="light" className="text-center border fst-italic">No specific anomalous records were found above the threshold or the sample is empty.</Alert>
            )}
        </div>

      </Card.Body>
      <Card.Footer className="text-muted small">
        Analysis completed on: {new Date().toLocaleString()}
      </Card.Footer>
    </Card>
  );
};

export default ResultsDisplay;
