import React, { useState } from 'react';
import { Form, Button, Spinner, Alert, ProgressBar } from 'react-bootstrap';
import axios from 'axios';

const FileUpload = ({ setUploadedFile, setError }) => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [localError, setLocalError] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setLocalError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!file) {
      setLocalError('Please select a file');
      return;
    }

    // Check file type
    const allowedTypes = [
      'text/csv', 
      'application/vnd.ms-excel', 
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    ];
    
    if (!allowedTypes.includes(file.type)) {
      setLocalError('Please upload a CSV or Excel file');
      return;
    }

    setUploading(true);
    setLocalError(null);
    setUploadProgress(0);
    
    // Create form data
    const formData = new FormData();
    formData.append('file', file);
    
    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(percentCompleted);
        }
      });
      
      setUploadedFile({
        filename: file.name,
        filepath: response.data.filepath
      });
      
      setUploading(false);
      setError(null);
    } catch (error) {
      setUploading(false);
      if (error.response) {
        setLocalError(error.response.data.error || 'Upload failed');
        setError(error.response.data.error || 'Upload failed');
      } else {
        setLocalError('Network error. Please check if the server is running.');
        setError('Network error. Please check if the server is running.');
      }
    }
  };

  return (
    <div className="file-upload-container">
      {localError && (
        <Alert variant="danger" className="mb-3">
          {localError}
        </Alert>
      )}
      
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Label>Select a CSV or Excel file with transaction data</Form.Label>
          <Form.Control 
            type="file" 
            onChange={handleFileChange}
            accept=".csv,.xls,.xlsx"
            disabled={uploading}
          />
          <Form.Text className="text-muted">
            Supported formats: CSV, Excel (.xlsx, .xls)
          </Form.Text>
        </Form.Group>

        {uploading && uploadProgress > 0 && (
          <ProgressBar 
            now={uploadProgress} 
            label={`${uploadProgress}%`}
            className="mb-3"
          />
        )}
        
        <Button 
          variant="primary" 
          type="submit"
          disabled={uploading || !file}
          className="upload-btn"
        >
          {uploading ? (
            <>
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
                className="me-2"
              />
              Uploading...
            </>
          ) : (
            'Upload File'
          )}
        </Button>
      </Form>
    </div>
  );
};

export default FileUpload;
