// src/components/ResultsDisplay.js
import React from 'react';
import { Card, Row, Col, Table, Image, Alert } from 'react-bootstrap';

/**
 * Componente para mostrar los resultados del análisis, incluyendo gráficos.
 * @param {object} props
 * @param {object} props.data - El objeto completo devuelto por /api/analyze (contiene 'results' y 'plots').
 */
const ResultsDisplay = ({ data }) => {
  // Extraer resultados y plots del objeto 'data' de forma segura
  const results = data?.results;
  const plots = data?.plots; // Diccionario con los gráficos base64

  // Mensaje si no hay datos de resultados válidos
  if (!results || typeof results !== 'object') {
    return (
        <Card className="results-card mt-4">
             <Card.Body>
                 <Card.Title>Analysis Results</Card.Title>
                 <p className="text-muted fst-italic">Waiting for analysis results or analysis returned invalid data.</p>
             </Card.Body>
        </Card>
    );
  }

  // Función auxiliar para formatear valores en la tabla (mejorada)
  const formatValue = (value) => {
    if (value === null || typeof value === 'undefined') return <span className="text-muted">N/A</span>;
    if (typeof value === 'number') {
      // Formatear floats con precisión controlada, evitar notación científica innecesaria
      if (value % 1 !== 0) {
          // Si es muy pequeño o muy grande, usar exponencial, sino 4 decimales
          if (Math.abs(value) > 1e6 || (Math.abs(value) < 1e-3 && value !== 0)) {
              return value.toExponential(2);
          }
          return value.toFixed(4);
      }
      return value.toLocaleString(); // Añadir separadores de miles a enteros
    }
    if (typeof value === 'boolean') {
        // Usar badges para booleanos
        return value
            ? <span className="badge bg-danger">Yes</span>
            : <span className="badge bg-secondary">No</span>;
    }
    // Acortar strings muy largos y añadir tooltip si se desea (más complejo)
    const strValue = String(value);
    if (strValue.length > 100) { return strValue.substring(0, 97) + '...'; }
    return strValue;
  };

  // Obtener columnas para la tabla (asegurándose de que haya registros)
  const anomalyRecords = results.sample_anomalous_records || [];
  const anomalyColumns = anomalyRecords.length > 0
    ? Object.keys(anomalyRecords[0])
    : [];

  // Función para formatear nombres de columnas (Título Amigable)
  const formatTitle = (key) => key?.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()) ?? 'N/A';


  return (
    <Card className="results-card mt-4 shadow-sm"> {/* Sombra sutil */}
      <Card.Header as="h5">Analysis Results</Card.Header> {/* Usar Card.Header */}
      <Card.Body>
        {/* Resumen Numérico */}
        <Alert variant="light" className="results-summary border mb-4"> {/* Borde sutil */}
            <Row className="align-items-center">
                <Col md={4} className="mb-2 mb-md-0 border-end"> {/* Añadir borde separador */}
                    <div><strong>Records Analyzed:</strong></div>
                    <div className="fs-5">{results.total_records_analyzed?.toLocaleString() ?? 'N/A'}</div>
                </Col>
                <Col md={4} className="mb-2 mb-md-0 border-end">
                    <div><strong>Anomalies Found:</strong></div>
                    <div className="fs-5 text-danger">{results.detected_anomalies_count?.toLocaleString() ?? 'N/A'} ({results.detected_anomalies_percentage ?? 'N/A'}%)</div>
                </Col>
                <Col md={4} className="mb-2 mb-md-0">
                    <div> <strong>Threshold (MSE):</strong></div>
                     <div className="fs-5">{(results.anomaly_threshold ?? 0).toExponential(4)}</div>
                </Col>
            </Row>
             <hr />
             <Row>
              <Col><small><strong>Columns Used in Analysis:</strong> {results.columns_analyzed?.join(', ') ?? 'N/A'}</small></Col>
             </Row>
        </Alert>

        {/* --- Sección de Gráficos Modificada --- */}
        {(plots && Object.keys(plots).length > 0) ? (
          <div className="plots-container mb-4">
            <h5 className="mb-3">Analysis Insights</h5>
            <Row>
              {/* Gráfico 1: Distribución Score */}
              {plots.anomaly_score_distribution && (
                <Col lg={6} className="mb-4"> {/* Usar lg para pantallas más grandes */}
                   <Card>
                     <Card.Header className="text-center small">Anomaly Score Distribution</Card.Header>
                     <Card.Body className="text-center">
                        <Image src={`data:image/png;base64,${plots.anomaly_score_distribution}`} fluid thumbnail alt="Anomaly Score Distribution" />
                        <p className="text-muted small mt-2">Distribution of anomaly scores (MSE). Red line is the threshold.</p>
                     </Card.Body>
                   </Card>
                </Col>
              )}

              {/* Gráfico 2: Distribución Numérica */}
              {plots.numerical_feature_distribution && results.plotted_numerical_feature && (
                <Col lg={6} className="mb-4">
                  <Card>
                    <Card.Header className="text-center small">Distribution for "{formatTitle(results.plotted_numerical_feature)}"</Card.Header>
                    <Card.Body className="text-center">
                        <Image src={`data:image/png;base64,${plots.numerical_feature_distribution}`} fluid thumbnail alt={`Distribution of ${results.plotted_numerical_feature}`} />
                         <p className="text-muted small mt-2">Compares Normal (blue area) vs. Anomalous (orange area) records.</p>
                    </Card.Body>
                  </Card>
                </Col>
              )}

              {/* Gráfico 3: Distribución Categórica */}
              {plots.categorical_feature_distribution && results.plotted_categorical_feature && (
                 <Col lg={6} className="mb-4">
                   <Card>
                     <Card.Header className="text-center small">Frequency for "{formatTitle(results.plotted_categorical_feature)}" (Top 15)</Card.Header>
                     <Card.Body className="text-center">
                         <Image src={`data:image/png;base64,${plots.categorical_feature_distribution}`} fluid thumbnail alt={`Frequency of ${results.plotted_categorical_feature}`} />
                          <p className="text-muted small mt-2">Compares category counts for Normal (blue) vs. Anomalous (orange) records.</p>
                     </Card.Body>
                   </Card>
                 </Col>
              )}
            </Row>
          </div>
        ) : (
            <Alert variant="secondary" className="text-center">No plots were generated for this analysis.</Alert>
        )}
        {/* --- Fin Sección de Gráficos Modificada --- */}


        {/* Tabla de Registros Anómalos */}
        <div className="anomaly-records mt-4">
            <h5 className="mb-3">Sample Anomalous Records <small className="text-muted">(Top 10 by Anomaly Score)</small></h5>
            {anomalyRecords.length > 0 ? (
             <div className="table-responsive">
                <Table striped bordered hover responsive="sm" size="sm">
                 <thead className="table-light"> {/* Cabecera más clara */}
                     <tr>{anomalyColumns.map((key) => (<th key={key}>{formatTitle(key)}</th>))}</tr>
                 </thead>
                 <tbody>
                 {anomalyRecords.map((record, index) => (
                    // Usar índice original como key si está presente y es único
                    <tr key={record.original_index ?? `anomaly-${index}`}>
                     {anomalyColumns.map((key) => (
                         <td key={`${record.original_index ?? index}-${key}`}
                             className={key === 'anomaly_score_mse' ? 'fw-bold' : (key === 'is_anomaly' ? 'text-center' : '')}> {/* Resaltar score y centrar booleano */}
                           {formatValue(record[key])}
                         </td>
                     ))}
                    </tr>
                  ))}
                 </tbody>
                </Table>
              </div>
            ) : (
                <Alert variant="secondary" className="text-center fst-italic">No specific anomalous records were found above the threshold or the sample is empty.</Alert>
            )}
        </div>

      </Card.Body>
      <Card.Footer className="text-muted small">
        Analysis completed on: {new Date().toLocaleString()} {/* Añadir timestamp */}
      </Card.Footer>
    </Card>
  );
};

export default ResultsDisplay;
